mapper.insert:
insert into user_info (user_id, user_nickname, user_sex, 
  user_level, time_create, img_avatar
  )
values (#{userId,jdbcType=INTEGER}, #{userNickname,jdbcType=VARCHAR}, #{userSex,jdbcType=INTEGER}, 
  #{userLevel,jdbcType=INTEGER}, #{timeCreate,jdbcType=TIMESTAMP}, #{imgAvatar,jdbcType=VARCHAR}
  )

mapper.select:
